  CREATE OR REPLACE TRIGGER "PORTAFOLIO"."TR_ACTUALIZAR_STOCK" 
before insert or update of cantidad or delete on detalles_boleta
for each row
declare
v_total numeric;
begin
if inserting then
update producto
set stock_unidad = stock_unidad - :new.cantidad
where cod_producto = :new.cod_producto;
end if;
if updating then
if :new.cantidad > :old.cantidad then
v_total := :new.cantidad-:old.cantidad;
update producto
set stock_unidad = stock_unidad - v_total
where cod_producto = :new.cod_producto;
else
v_total := :old.cantidad - :new.cantidad;
update producto
set stock_unidad = stock_unidad + v_total
where cod_producto = :new.cod_producto;
end if;
end if;
if deleting then
update producto
set stock_unidad = stock_unidad + :old.cantidad
where cod_producto = :old.cod_producto;
end if;
end;
/
ALTER TRIGGER "PORTAFOLIO"."TR_ACTUALIZAR_STOCK" ENABLE;
