--------------------------------------------------------
--  DDL for Procedure SP_ACTUALIZAR_USUARIO
--------------------------------------------------------
set define off;

  CREATE OR REPLACE PROCEDURE "PORTAFOLIO"."SP_ACTUALIZAR_USUARIO" (V_RUT_EMPLEADO IN EMPLEADOS.RUT_EMPLEADO%TYPE,
V_SUELDO_BASE IN EMPLEADOS.SUELDO_BASE%TYPE, V_NOMBRE IN PERSONA.NOMBRE%TYPE,
V_APELLIDOS IN PERSONA.APELLIDOS%TYPE,
V_DIRECCION IN PERSONA.DIRECCION%TYPE,
V_TELEFONO IN PERSONA.TELEFONO%TYPE, V_CORREO IN PERSONA.CORREO%TYPE,
V_CONTRASEÑA IN USUARIOS.CONTRASEÑA%TYPE)
is
V_USUARIO USUARIOS.USUARIO%TYPE;
V_ID_PERSONA PERSONA.ID_PERSONA%TYPE;
V_DESC_IMPOSICIONES EMPLEADOS.DESC_IMPOSICIONES%TYPE;
V_SUELDO_LIQUIDO EMPLEADOS.SUELDO_LIQUIDO%TYPE;
begin
V_DESC_IMPOSICIONES := (V_SUELDO_BASE*0.23);
V_SUELDO_LIQUIDO := (V_SUELDO_BASE - V_DESC_IMPOSICIONES);
SELECT E.USUARIO, ID_PERSONA
INTO V_USUARIO, V_ID_PERSONA
FROM EMPLEADOS E
JOIN USUARIOS U
ON E.USUARIO = U.USUARIO
WHERE RUT_EMPLEADO = V_RUT_EMPLEADO;
UPDATE USUARIOS 
SET CONTRASEÑA = V_CONTRASEÑA
    WHERE USUARIO = V_USUARIO;

UPDATE EMPLEADOS 
SET SUELDO_BASE = V_SUELDO_BASE,
desc_imposiciones = v_desc_imposiciones,
sueldo_liquido = v_sueldo_liquido
WHERE USUARIO = V_USUARIO;
UPDATE PERSONA
SET  NOMBRE = V_NOMBRE,
    APELLIDOS = V_APELLIDOS,
    DIRECCION = V_DIRECCION,
    TELEFONO = V_TELEFONO,
    CORREO = V_CORREO
    WHERE ID_PERSONA = V_ID_PERSONA;
end;

