  CREATE OR REPLACE PROCEDURE "PORTAFOLIO"."SP_CREAR_FIADO" (V_RUT_CLIENTE CLIENTES.RUT_CLIENTE%TYPE,
V_NOMBRE PERSONA.NOMBRE%TYPE, V_APELLIDOS PERSONA.APELLIDOS%TYPE, V_DIRECCION PERSONA.DIRECCION%TYPE,
V_TELEFONO PERSONA.TELEFONO%TYPE, V_CORREO PERSONA.CORREO%TYPE, V_NBOLETA BOLETA.N_BOLETA%TYPE,
V_ABONADO DEUDA.ABONADO%TYPE, V_FECHA_LIMITE DEUDA.FECHA_LIMITE%TYPE, V_TOTAL IN NUMBER)
IS
V_ID_PERSONA PERSONA.ID_PERSONA%TYPE;
V_DEUDA_PENDIENTE DEUDA.DEUDA_PENDIENTE%TYPE;
BEGIN
V_ID_PERSONA := SEQ_CLIENTE.NEXTVAL;
INSERT INTO CLIENTES (RUT_CLIENTE, ID_PERSONA) VALUES ( V_RUT_CLIENTE, V_ID_PERSONA);
INSERT INTO PERSONA (ID_PERSONA, NOMBRE, APELLIDOS, DIRECCION, TELEFONO, CORREO)
VALUES (V_ID_PERSONA, V_NOMBRE, V_APELLIDOS, V_DIRECCION, V_TELEFONO, V_CORREO);

V_DEUDA_PENDIENTE := V_TOTAL - V_ABONADO;

INSERT INTO DEUDA (ID_DEUDA, FECHA_FIADO, FECHA_LIMITE, ABONADO, DEUDA_PENDIENTE, REALIZADO, RUT_CLIENTE, N_BOLETA)
VALUES (SEQ_DEUDA.NEXTVAL,SYSDATE, V_FECHA_LIMITE, V_ABONADO,V_DEUDA_PENDIENTE, 'N', V_RUT_CLIENTE, V_NBOLETA);

UPDATE BOLETA
SET FORMA_PAGO = 'FIADO',
ABONADO = V_ABONADO,
REALIZADO = 'N',
RUT_CLIENTE = V_RUT_CLIENTE
WHERE N_BOLETA = V_NBOLETA;

END;

